$(document).ready(function(){var a=$("#loginform");a.submit(function(b){$("#login-failure").addClass("hidden");b.preventDefault();$.post(a.attr("action"),a.serialize(),onLoginSuccess).fail(function(a,b,e){alert("An unexpected error occured. Please try again later or contact the webmaster.")})})});
function onLoginSuccess(a,b,c,d){if(!0===a.success)if("function"===typeof window["post-authenticate"])window["post-authenticate"]();else location.reload(!0);else if($("#login-failure").removeClass("hidden"),"function"===typeof window["failed-authentication"])window["failed-authentication"]()};
